#!/usr/bin/env node
const fs=require("fs"),dir="./app/api",inquirer=require("inquirer"),questions=[{type:"input",name:"folderName",message:"Mau buat folder api routes untuk endpoint apa ?"}],nameFilesFs=(e,r,i)=>{fs.appendFile(e+`/${r}.${i}.js`,"// welcome",function(e){if(e)throw e})},createFileFs=(r,i)=>{["controller","services","routes"].forEach(e=>{nameFilesFs(r,i,e)})},createFolderFs=e=>{var r=dir+"/"+e;fs.existsSync(dir+"/"+e)||(fs.mkdirSync(dir+"/"+e,{recursive:!0}),console.log("Created !"),createFileFs(r,e))},createApi=inquirer.prompt(questions).then(e=>{createFolderFs(e.folderName)});module.exports=createApi;